import numpy as np, pandas as pdnp.random.seed(42)themes = {    "Ease of Use & Learnability": {"mean": 4.1169, "std": 0.2709},    "System Efficiency & Learning Burden": {"mean": 4.1240, "std": 0.0910},    "Perceived Complexity & Integration": {"mean": 3.7100, "std": 0.2160}}num_students = 10000df = pd.DataFrame({    theme: np.clip(np.random.normal(loc=val["mean"], scale=val["std"], size=num_students), 1, 5).round(4)    for theme, val in themes.items()})pd.set_option('display.max_columns', None)print(df.head(10))